                System.Diagnostics.Debug.WriteLine(e.Exception);
            };
#endif



            var app= builder.Build();

            using (var scope = app.Services.CreateScope())
            {
                var db = scope.ServiceProvider.GetRequiredService<KonciergeDbContext>();
                db.Database.Migrate();
            }


            return app;
        }
    }
}
